# -*- coding:utf-8 -*-

import numpy as np
from py5gphy.crc import crc

def polar_cbsegment(uciBits, Euci):
    """polar code block segment by TS38.212 5.2.1
        only UL UCI bits use poalr code block segment by 6.3.1.2.1 and 6.3.2.2
    input:
        uciBits: A length UCI bits
        Euci: uci total rate match output bit length, refer to 6.3.1.4.1 UCI encoded by Polar code
    output:
        polar_cb_blks: C by (A+L) array of blocks after adding CRC
        C: number of CB 
        Er: rate match size for each code block
    """
    A = uciBits.size

    assert (A >= 12) and (A <= 1706) 
    
    #38.212 6.3.1.2.1 and 5.2.1
    if (A >= 1013) or ((A >= 360) and (Euci >= 1088)):
        assert (Euci % 2) == 0
        C = 2
        if A % 2:
            paddedUCI = np.zeros(A+1,'i1')
            paddedUCI[1:] = uciBits
        else:
            paddedUCI = uciBits
        Ar = paddedUCI.size // 2
        uciCBs = np.zeros((C,Ar),'i1')
        uciCBs[0] = paddedUCI[0:Ar]
        uciCBs[1] = paddedUCI[Ar :]
        crcpoly = '11'
        L = 11
    else:
        C = 1
        Ar = uciBits.size
        uciCBs = np.zeros((C, Ar),'i1')
        uciCBs[0] = uciBits
        if A <= 19:
            crcpoly = '6'
            L = 6
        else:
            crcpoly = '11'
            L = 11
    
    Er = Euci // C
    polar_cb_blks = np.zeros((C, Ar+L), 'i1')
    for m in range(C):
        blkandcrc = crc.nr_crc_encode(uciCBs[m], crcpoly, 0)
        polar_cb_blks[m,:] = blkandcrc
    
    return polar_cb_blks, C, Er

